create table posts (post_id integer primary key, points integer, title text, domain text, username text, url text, num_comments integer, creation_date date);
create table post_uses (post_id integer, use_date date, use_tod text, FOREIGN KEY (post_id) REFERENCES posts (post_id) ON DELETE cascade, check (use_tod in ('midnight', 'morning', 'noon', 'evening', 'bogus')), primary key (post_id, use_date, use_tod));
